import styles from './Register.module.css';
import React, {useState} from 'react';


const Register = () => {
  // состояния полей формы
  const [email, setEmail] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  // состояния ошибок валидации
  const [emailError, setEmailError] = useState('');
  const [usernameError, setUsernameError] = useState('');
  const [passwordError, setPasswordError] = useState('');

  // функция для валидации email
  const validateEmail = email => {
    const emailRegex = /^\S+@\S+\.\S+$/;
    if (!email) {
      setEmailError('Email is required');
    } else if (!emailRegex.test(email)) {
      setEmailError('Please enter a valid email');
    } else {
      setEmailError('');
    }
  };

  // функция для валидации username
  const validateUsername = username => {
    if (!username) {
      setUsernameError('Username is required');
    } else if (username.length < 3) {
      setUsernameError('Username must be at least 3 characters long');
    } else {
      setUsernameError('');
    }
  };

  // функция для валидации password
  const validatePassword = password => {
    if (!password) {
      setPasswordError('Password is required');
    } else if (password.length < 6) {
      setPasswordError('Password must be at least 6 characters long');
    } else {
      setPasswordError('');
    }
  };

  // функция для обработки отправки формы
  const handleSubmit = event => {
    event.preventDefault();
    // проверяем, что все поля валидные
    validateEmail(email);
    validateUsername(username);
    validatePassword(password);
    if (emailError || usernameError || passwordError) {
      return; // если есть ошибки валидации, не отправляем форму
    }
    // Отправляем данные на сервер или обрабатываем их как угодно
    console.log('Email:', email, 'Username:', username, 'Password:', password);
  };

  // Обработчики изменения состояний полей формы
  const handleEmailChange = event => setEmail(event.target.value);
  const handleUsernameChange = event => setUsername(event.target.value);
  const handlePasswordChange = event => setPassword(event.target.value);

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Email:
        <input type="text" value={email} onChange={handleEmailChange} />
        {emailError && <div style={{ color: 'red' }}>{emailError}</div>}
      </label>
      <label>
        Username:
        <input type="text" value={username} onChange={handleUsernameChange} />
        {usernameError && <div style={{ color: 'red' }}>{usernameError}</div>}
      </label>
      <label>
        Password:
        <input type="password" value={password} onChange={handlePasswordChange} />
        {passwordError && <div style={{ color: 'red' }}>{passwordError}</div>}
      </label>
      <button type="submit">Submit</button>
    </form>
  );
};

export default Register
